  <span class="token punctuation">-</span> <span class="token punctuation">-</span> meta
    <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> things<span class="token punctuation">:</span>not<span class="token punctuation">-</span>to<span class="token punctuation">-</span>do<span class="token punctuation">-</span><span class="token number">7</span>
      <span class="token key atrule">content</span><span class="token punctuation">:</span> tell a lie
  <span class="token punctuation">-</span> <span class="token punctuation">-</span> meta
    <span class="token punctuation">-</span> <span class="token key atrule">name</span><span class="token punctuation">:</span> things<span class="token punctuation">:</span>not<span class="token punctuation">-</span>to<span class="token punctuation">-</span>do<span class="token punctuation">-</span><span class="token number">8</span>
      <span class="token key atrule">content</span><span class="token punctuation">:</span> hurt you
